import { createSlice } from "@reduxjs/toolkit";

const todoSlices = createSlice({
    name: "task",
    initialState: [],
    reducers: {
        addTask: (state, action) => {
            state.push(action.payload)
        }
    },
})

//actions
export const {addTask} = todoSlices.actions
//reducers

export default todoSlices.reducer
