import Card from 'react-bootstrap/Card'
import Button from 'react-bootstrap/Button'
import { useDispatch } from 'react-redux'
import { deleteTask } from '../../redux/slices/todoSlices'

const CardTodo = ( { title, date, description, state } ) => {
    
    const dispatch = useDispatch()  
    
    const removeTask = () => {
        dispatch(
            deleteTask({
                date: date,
                state: state
            }) 
        )       
    }

    return (
        <Card className="mb-2" bg='warning' style={{width: '16rem'}}>
            <Card.Header>
                <Card.Title><i className="bi bi-card-text"></i>{title}</Card.Title>
            </Card.Header>
            <Card.Body>
                <Card.Subtitle>
                    <i className="bi bi-calendar-week"></i> {date}
                </Card.Subtitle>
                <Card.Text>
                    {description}
                </Card.Text>
            </Card.Body>
            <Card.Footer>
                <Button variant="danger" onClick={removeTask}><i className="bi bi-trash"></i> Eliminar</Button>
            </Card.Footer>
        </Card>
    )
}

export default CardTodo
