import Card from 'react-bootstrap/Card'
import Col from 'react-bootstrap/Col'
import Button from 'react-bootstrap/Button'
import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row'
import { useDispatch, useSelector } from 'react-redux'
import { deleteTask, toggleTask } from '../../redux/slices/todoSlices';

const CardTodo = ( {id, done, date, description} ) => {

    const todos = useSelector( (state) => state.todo.todos )
    const dispatch = useDispatch()
    
    const eventComplete = (id) => {
        dispatch(toggleTask(id))
    }

    const eventDelete = (id) => {
        dispatch(deleteTask(id))
    }
    
    return (
        <Card className="mb-2" bg='warning' style={{width: '16rem'}}>
            <Card.Body>
                <Card.Subtitle><i className="bi bi-calendar-week"></i> {date}</Card.Subtitle>
                <Card.Text>{description}</Card.Text>
            </Card.Body>
            <Card.Footer>
                <Row>
                    <Col>
                        <Button variant="danger" style={{fontSize: '.8em'}} onClick={eventDelete}><i className="bi bi-trash"></i> Eliminar</Button>
                    </Col>
                    <Col>
                        <Form.Check type="switch" id="custom-switch" label="Completo" onClick={eventComplete}/>
                    </Col>
                </Row>
            </Card.Footer>
        </Card>
    )
}

export default CardTodo
