import { useState } from 'react'
import { useSelector, useDispatch } from 'react-redux'
import Form from 'react-bootstrap/Form'
import Label from 'react-bootstrap/FloatingLabel'
import Button from 'react-bootstrap/Button'
import { addTask } from '../../redux/slices/todoSlices'

const TaskTodo = () => {

  const [input, setInput] = useState("") 
  const todos = useSelector( (state) => state.todo )
  const dispatch = useDispatch()

  const handler = () => {
    dispatch(addTask(input))
  }

  return (
    <Form>
      <Form.Group className="mb-3">
        <Label label="Titulo" className="mb-3">
          <Form.Control type="text"/>
        </Label>
        <Label label="Descripción">
          <Form.Control as="textarea" rows={3} value={input} onChange={ (e) => setInput(e.target.value)}/>
        </Label>
      </Form.Group >
      <Button variant="primary" onClick={handler}>Añadir tarea</Button>
    </Form>
  )
}

export default TaskTodo
