import Card from 'react-bootstrap/Card'
import Button from 'react-bootstrap/Button'
import { useDispatch } from 'react-redux'
import { deleteTask } from '../../redux/slices/todoSlices'

const CardTodo = ( {date, description} ) => {

    const dispatch = useDispatch()
    const removeTask = () => {
        dispatch(
            deleteTask(
                {id: date}
            )
        )
    } 
    
    return (
        <Card className="mb-2" bg='warning' style={{width: '16rem'}}>
            <Card.Body>
                <Card.Subtitle><i className="bi bi-calendar-week"></i> {date}</Card.Subtitle>
                <Card.Text>{description}</Card.Text>
            </Card.Body>
            <Card.Footer>
                <Button variant="danger" onClick={removeTask}><i className="bi bi-trash"></i> Eliminar</Button>
            </Card.Footer>
        </Card>
    )
}

export default CardTodo
