import { useDispatch } from 'react-redux'
import { useState } from 'react'
import Button from 'react-bootstrap/Button'
import Form from 'react-bootstrap/Form'
import Label from 'react-bootstrap/FloatingLabel'
import { addTask } from '../../redux/slices/todoSlices'

const AddTask = () => {

  const [text, setText] = useState("")
  const dispatch = useDispatch()


  const onSubmit = (event) => {
    event.preventDefault()
    if(text.length === 0) {
      alert("Debes dejar una actividad para guardar")
    }
    dispatch(addTask(text))
  }

  return (
    <Form className="mb-3">
      <Form.Group>
        <Label label="Describe tu tarea" className="mb-3">
          <Form.Control as="textarea" rows={3} cols={60} value={text} onChange={(event)=>setText(event.target.value)}/>
        </Label>
      </Form.Group >
      <Button variant="primary" onClick={onSubmit} ><i className="bi bi-pencil"></i> Agregar</Button>
    </Form>
  )
}

export default AddTask
