import { useDispatch } from 'react-redux'
import { useState } from 'react'
import Form from 'react-bootstrap/Form'
import Label from 'react-bootstrap/FloatingLabel'
import Button from 'react-bootstrap/Button'
import { addTask } from '../../redux/slices/todoSlices'

const AddTask = () => {

  const [value, setValue] = useState("")
  const dispatch = useDispatch()


  const onSubmit = (event) => {

    if(value.length == undefined || value.length == 0) {
      alert("Añade una tarea para continuar")
      return setValue("")
    }

		dispatch(
			addTask({
				task: value
			})
		)
  }

  return (
    <Form className="mb-3">
      <Form.Group>
        <Label label="Describe tu tarea" className="mb-3">
          <Form.Control as="textarea" rows={3} cols={60} value={value} onChange={(event)=>setValue(event.target.value)}/>
        </Label>
      </Form.Group >
      <Button variant="primary" onClick={onSubmit} ><i className="bi bi-pencil"></i> Agregar</Button>
    </Form>
  )
}

export default AddTask
