import { useDispatch } from 'react-redux'
import { useState } from 'react'
import Form from 'react-bootstrap/Form'
import Label from 'react-bootstrap/FloatingLabel'
import Button from 'react-bootstrap/Button'
import { addTask } from '../../redux/slices/todoSlices'

const AddTask = () => {

  const [value, SetValue] = useState("")
  const dispatch = useDispatch()


  const onSubmit = (event) => {
    
    event.preventDefault()

		if(value.trim().length === 0)
		{
			alert("Enter a task before adding !!")
			SetValue("")
			return
		}

		dispatch(
			addTask({
				task: value
			})
		)

    SetValue("")
  }

  return (
    <Form className="mb-3">
      <Form.Group>
        <Label label="Titulo" className="mb-3">
          <Form.Control type="text" value={value} onChange={(event)=>SetValue(event.target.value)}/>
        </Label>
        <Label label="DescripciÃ³n" className="mb-3">
          <Form.Control as="textarea" rows={3} cols={60}/>
        </Label>
      </Form.Group >
      <Button variant="primary" onClick={onSubmit} ><i className="bi bi-pencil"></i> Agregar</Button>
    </Form>
  )
}

export default AddTask
