import { createSlice } from "@reduxjs/toolkit";

const initialState = [
    {
        title: "",
        date: "",
        description: "",
        state: true
    }
]

const todoSlices = createSlice({
    name: "task",
    initialState,
    reducers: {
        addTask: (state, action) => {
            state.push(action.payload.task)
        },
        deleteTask: (state, action) => {
            state.filter((item) => item.state !== action,item)
        }
    },
})

//actions
export const {addTask, deleteTask} = todoSlices.actions
//reducers
export default todoSlices.reducer
