import { createSlice } from "@reduxjs/toolkit";

const todoSlices = createSlice({
    name: "task",
    initialState: [],
    reducers: {
        addTask: (state, action) => {
            
            const writeTask = {
                title: action.payload.task,
                date: new Date(),
                description: action.payload.task
            }

            state.push(writeTask)
            console.log(writeTask)
        },
        deleteTask: (state, action) => {
            state.filter((item) => item.state !== action)
        }
    },
})

//actions
export const {addTask, deleteTask} = todoSlices.actions
//reducers
export default todoSlices.reducer
